import React, {useState} from 'react';
import { Text, View, StyleSheet, TextInput, Button,TouchableHighlight} from 'react-native';
const Form = ({addHandler}) => {
  const [text, setValue] = useState ('');
  const onChange = (text) => {
    setValue (text);

  };
  return (
    <View>
      <TextInput style = {styles.input} onChangeText = {onChange} placeholder = 'Впишите планы'/>
      <Button color = 'green' onPress = {() => addHandler (text)} title = 'Добавить'/>
    </View>
   );
};
export default Form;
const styles = StyleSheet.create({
  input:
  {
    borderBottomWidth: 1,
    borderColor: 'black',
    padding: 10,
    marginVertical : 30,  
    marginHorizontal: '20%',
    width: '60%',
  }
  });
